package co.edu.unac.poo1.bebidas;public abstract class Bebida {    protected String nombre;    protected Float tamaho;    protected Float precio;    public Bebida() {    }    public Bebida(String nombre, Float tamaho, Float precio) {        this.nombre = nombre;        this.tamaho = tamaho;        this.precio = precio;    }    public Float getPrecio() {        return precio;    }    public void setPrecio(Float precio) {        this.precio = precio;    }    public String getNombre() {        return nombre;    }    public void setNombre(String nombre) {        this.nombre = nombre;    }    public Float getTamaho() {        return tamaho;    }    public void setTamaho(Float tamaho) {        this.tamaho = tamaho;    }    public void verInformacion2() {        System.out.println("-----------------------------");        System.out.println("ver Nombre: " + this.nombre);        System.out.println("ver precio: " + this.precio);        System.out.println("-----------------------------");    }}