package co.edu.unac.poo1.bebidas;import co.edu.unac.poo1.alimentos.Sabores;import co.edu.unac.poo1.procesos.Madurados;import java.util.Objects;public class Cerveza extends Bebida implements Madurados {    private String serial;    private Sabores sabores;    public Cerveza() {        super();    }    public Cerveza(String nombre, Float tamaho, Float precio, String serial, Sabores sabores) {        super(nombre, tamaho, precio);        this.serial = serial;        this.sabores = sabores;    }    public String getSerial() {        return serial;    }    public void setSerial(String serial) {        this.serial = serial;    }    public Sabores getSabores() {        return sabores;    }    public void setSabores(Sabores sabores) {        this.sabores = sabores;    }    @Override    public boolean agregarAlcohol() {        return false;    }    @Override    public void verInformacion() {    }    @Override    public boolean equals(Object obj) {        if(!obj.getClass().equals(Cerveza.class)){            return false;        } else {            Cerveza CervezaObj = (Cerveza) obj;            return this.nombre.equals(CervezaObj.nombre) &&                    Objects.equals(this.sabores, CervezaObj.sabores) &&                    this.tamaho.equals(CervezaObj.tamaho);        }    }}