package co.edu.unac.poo1.bebidas;public class Milo extends CafeMoka implements Chocolatosas{    private Float cantidadGramosLeche;    public Milo() {        super();    }    public Milo(String nombre, Float tamaho, Float precio, String serial, Integer altura, Integer edad, String variedad, String sabor, Float cantidadGramosLeche) {        super(nombre, tamaho, precio, serial, altura, edad, variedad, sabor);        this.cantidadGramosLeche = cantidadGramosLeche;    }    public Float getCantidadGramosLeche() {        return cantidadGramosLeche;    }    public void setCantidadGramosLeche(Float cantidadGramosLeche) {        this.cantidadGramosLeche = cantidadGramosLeche;    }    @Override    public void verInformacion2() {        System.out.println("Nombre " + this.nombre);        System.out.println("Porcentaje de leche " + this.cantidadGramosLeche + "Gramos");    }}